mapper.select:

select

from sys_member

  1=1

    and MEMBER_ID = #{memberId,jdbcType=VARCHAR};


    and MEMBER_NAME = #{memberName,jdbcType=VARCHAR}


    and MEMBER_TEL = #{memberTel,jdbcType=VARCHAR}


    and MEMBER_PASSWORD = #{memberPassword,jdbcType=VARCHAR}

select

from sys_member

  1=1

    and MEMBER_NAME = #{memberName,jdbcType=VARCHAR}


    and MEMBER_EMAIL = #{memberEmail,jdbcType=VARCHAR}


    and MEMBER_TEL LIKE '%' #{memberTel,jdbcType=VARCHAR} '%'


    and CREATE_TIME  >=  #{startDate,jdbcType=VARCHAR}


    and CREATE_TIME  <=  #{endDate,jdbcType=VARCHAR}


LIMIT #{pageStart},#{pageEnd}

select
count(*) as count
from sys_member

  1=1

    and MEMBER_NAME = #{memberName,jdbcType=VARCHAR}


    and MEMBER_EMAIL = #{memberEmail,jdbcType=VARCHAR}


    and MEMBER_TEL LIKE '%' #{memberTel,jdbcType=VARCHAR} '%'


    and CREATE_TIME  >=  #{startDate,jdbcType=VARCHAR}


    and CREATE_TIME  <=  #{endDate,jdbcType=VARCHAR}
